import { CARD } from "./CaseStudyCardInfo/caseConstants";
import CaseStudyCard from "./CaseStudyCardInfo/CaseStudyCard";
import React, { useRef } from "react";
import leftScroll from "../../../img/tempLeftArrow.svg";
import rightScroll from "../../../img/tempRightArrow.svg";

export default function CaseStudy() {
    const containerRef = useRef(null);

    // Scroll left
    const scrollLeft = () => {
        if (containerRef.current) {
            containerRef.current.scrollBy({
                left: -300,
                behavior: "smooth",
            });
        }
    }
    // Scroll right
    const scrollRight = () => {
        if (containerRef.current) {
            containerRef.current.scrollBy({
                left: +300,
                behavior: "smooth",
            });
        }
    }

    const totalCardWidth = CARD.length * 296; // Assuming each card is 300px wide

    const containerStyle = {
      marginLeft: CARD.length * 300 > window.innerWidth ? '' : 'auto',
      marginRight: CARD.length * 300 > window.innerWidth ? '' : 'auto',
    };


  return (
    <div>
      <div className="border-b border-solid border-b-gray-400">
        <div className="flex justify-between items-center align-middle text-white text-xs ml-5 my-10 font-bold">
            <h6 className="w-32">
                /Case Studies
            </h6>
            <div className="flex justify-end text-blue-500 text-lg w-screen h-5 mr-10">
                <img src={leftScroll} alt="Left Scroll Arrow" className={`hover:cursor-pointer mx-10 ${CARD.length * 296 > window.innerWidth ? '' : 'hidden'}`} onClick={scrollLeft}/>
                <img src={rightScroll} alt="Right Scroll Arrow" className={`hover:cursor-pointer ${CARD.length * 296 > window.innerWidth ? '' : 'hidden'}`} onClick={scrollRight}/>
            </div>
        </div>
        <div className="flex flex-nowrap justify-start w-full h-3/4 text-2xl pb-14 mt-4 mx-auto overflow-x-hidden" ref={containerRef}>
            {CARD.map((card) => (
              <CaseStudyCard
                key={card.key}
                image={card.img}
                header={card.header}
                alt={card.alt}
              />
            ))}
          </div>
        <div className="flex justify-center pb-14">
          <button className="text-white text-base px-4 py-2 font-bold bg-black border border-solid border-blue-500 rounded-lg hover:bg-blue-500 hover:text-white">
            Our Portfolio
          </button>
        </div>
      </div>
    </div>
  );
}
