import { CARD } from "./CaseStudyCardInfo/caseConstants";
import CaseStudyCard from "./CaseStudyCardInfo/CaseStudyCard";
import React, { useRef } from "react";
import leftScroll from "../../../img/tempLeftArrow.svg";
import rightScroll from "../../../img/tempRightArrow.svg";

export default function CaseStudy() {
    const containerRef = useRef(null);
    const [scrollingEnabled, setScrollingEnabled] = React.useState(true);

    // Scroll left
    const scrollLeft = () => {
        if (containerRef.current && scrollingEnabled) {
            containerRef.current.scrollLeft -= 200;
        }
    }
    // Scroll right
    const scrollRight = () => {
        if (containerRef.current && scrollingEnabled) {
            containerRef.current.scrollLeft += 200;
        }
    }

    const preventScrolling = (e) => {
        e.preventDefault();
    }

  return (
    <div>
      <div className="border-b border-solid border-b-gray-400">
        <div className="flex justify-between items-center align-middle text-white text-xs ml-5 my-10 font-bold">
            <h6 className="w-32">
                /Case Studies
            </h6>
            <div className="flex justify-end text-blue-500 text-lg w-screen h-5 mr-10">
                <img src={leftScroll} alt="Left Scroll Arrow" className="hover:cursor-pointer mx-10" onClick={scrollLeft}/>
                <img src={rightScroll} alt="Right Scroll Arrow" className="hover:cursor-pointer" onClick={scrollRight}/>
            </div>
        </div>
        <div className="flex flex-nowrap w-full h-3/4 text-2xl pb-14 mt-4 mx-auto overflow-x-auto" ref={containerRef} style={{ scrollBehavior:"smooth"}} onWheel={preventScrolling}>
            {CARD.map((card) => (
              <CaseStudyCard
                key={card.key}
                image={card.img}
                header={card.header}
                alt={card.alt}
              />
            ))}
          </div>
        <div className="flex justify-center pb-14">
          <button className="text-white text-base px-4 py-2 font-bold bg-black border border-solid border-blue-500 rounded-lg hover:bg-blue-500 hover:text-white">
            Our Portfolio
          </button>
        </div>
      </div>
    </div>
  );
}
