import React from 'react';
import testImage1 from "../../img/testImage1.svg";
import hamburgerIcon from "../../img/tempHamburgerIcon.svg";
import backgroundVideo from "../../img/testStockVideoBackground.mp4";

export default function OpeningScreen() {

    const videoBackground = {
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        zIndex: 0, // Place the video in the background
    };

    function handleClickJoin() {
        window.location = "https://airtable.com/appYvJkhyjQlVvoqV/shrXCWB0tMh79Ty5i";
    }

    return (
        <div className="w-screen h-screen flex flex-col items-start justify-start">
            <video autoPlay muted loop style={videoBackground}>
                <source src={backgroundVideo} type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div className="w-screen h-20 flex items-start justify-between">
                <div className="flex items-center">
                    <div>
                        <img src={testImage1} alt="testLogo" className="w-20 h-20 ml-3 pt-3" />
                    </div>
                    <div className="text-white px-8 py-8 text-3xl font-bold">
                        <p>Disrupt</p>
                    </div>
                </div>
                <div className="flex items-center py-8">
                    <img src={hamburgerIcon} alt="hamburgerMenuIcon" className="w-32 h-14 hover:scale-125 hover:cursor-pointer" />
                </div>
            </div>
            <div className="w-screen flex flex-col items-start pl-16 pt-80">
                <p className="text-white font-bold text-2xl pb-8 w-1/2">
                    Lorem ipsum dolor sit amet consectetur. Ac quis in auctor sed aliquam massa non. Porta massa sit vulputate in sed enim
                </p>
                <button className="text-white text-2xl font-bold border border-solid border-blue-500 px-4 py-2 rounded-lg hover:bg-blue-500"
                onClick={handleClickJoin}>
                    Join
                </button>
            </div>
        </div>
    )
};
